var urlParser = require('url');
var http = require('http');
var md5 = require('MD5');

/*
	DataMcFly class
	@database {String}
    @collection {String}
	@key {String} :: get your key from http://app.datamcfly.com/
*/
function DataMcFly(database, collection, apikey) {

	this.apikey = apikey;
	this.query = {};
	this.database = database;
	this.collection = collection;
	this.currentItem;
	this.room = md5( database + '/' + collection ); 		//	this will be a hash of the room..
	this.uri = urlParser.parse('http://api.datamcfly.com/apps/');
	this.push_uri = urlParser.parse('http://push.datamcfly.com');
	this.startWebSocket( this.room );
};

// ======================================================
// FUNCTIONS
// ======================================================

/*
	Check if string is JSON object
	@value {String}
	return {Object}
*/
function parseJSON(value) {
	if (isJSON(value))
		return JSON.parse(value);
	return {};
};

function isJSON(value) {
	if (value.length === 1)
		return false;
	var a = value[0];
	var b = value[value.length - 1];
	return (a === '"' && b === '"') || (a === '[' && b === ']') || (a === '{' && b === '}');
};

/*
	Object to URL params
	@obj {Object}
	return {String}
*/
function toParams(obj, apikey) {

	if (typeof(obj) === 'undefined' || obj === null)
		return '?apiKey=' + apikey;

	var buffer = [];

	var arr = Object.keys(obj);

	arr.forEach(function(o) {

		var value = obj[o];
		var name = o.toLowerCase().replace(/-/g, '');

		switch (name) {
			case 'q':
			case 'query':
			case 'where':
				buffer.push('q=' + encodeURIComponent(JSON.stringify(value)));
				break;		

			case 'include':

				if (typeof(value) === 'array') {
					var prepare = {};
					value.forEach(function(o) {
						prepare[o] = 1;
					});
					value = prepare;
				}

				buffer.push('f=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 'exclude':

				if (typeof(value) === 'array') {
					var prepare = {};
					value.forEach(function(o) {
						prepare[o] = 0;
					});
					value = prepare;
				}

				buffer.push('f=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 'f':
				buffer.push('f=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 'l':
			case 'max':
			case 'top':
			case 'limit':
			case 'take':
				buffer.push('l=' + value);
				break;

			case 'skip':
			case 'sk':
				buffer.push('sk=' + value);
				break;

			case 'asc':

				if (typeof(value) === 'array') {
					var prepare = {};
					value.forEach(function(o) {
						prepare[o] = 1;
					});
					value = prepare;
				}

				buffer.push('s=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 'desc':

				if (typeof(value) === 'array') {
					var prepare = {};
					value.forEach(function(o) {
						prepare[o] = -1;
					});
					value = prepare;
				}

				buffer.push('s=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 's':
			case 'sort':
			case 'sorting':
				buffer.push('s=' + encodeURIComponent(JSON.stringify(value)));
				break;

			case 'c':
			case 'count':
				buffer.push('c=' + value.toString().toLowerCase());
				break;

			case 'fo':
			case 'first':
				buffer.push('fo=' + value.toString().toLowerCase());
				break;

			case 'm':
			case 'updateall':
			case 'all':
				buffer.push('m=' + value.toString().toLowerCase());
				break;

			case 'u':
			case 'upsert':
				buffer.push('u=' + value.toString().toLowerCase());
				break;

			default:
				break;
		};
	});

	var params = buffer.join('&');
//	console.log( '?' + (params.length > 0 ? params + '&' : '') + 'apiKey=' + apikey );
	return '?' + (params.length > 0 ? params + '&' : '') + 'apiKey=' + apikey;
};

