DataMcFly.prototype.where = function( where ){
	this.query.q = where;
	return this;
}

DataMcFly.prototype.fields = function( field ){
	//	return only the field we passed as $field...
	this.query.f = field;
	return this;
}

DataMcFly.prototype.skip = function( n ){
	this.query.sk = n;
	return this;
}

DataMcFly.prototype.orderBy = function( o ){
	this.query.s = o;
	return this;
}

DataMcFly.prototype.limit = function( n ){
	this.query.l = n;
	return this;
}
DataMcFly.prototype.limitToFirst = function( n ){
	this.query.l = n;
//	this.query.s = {"_id": 1};
	return this;
}
DataMcFly.prototype.limitToLast = function( n ){
	this.query.l = n;
	this.query.s = {"_id": -1};
	return this;
}

DataMcFly.prototype.onDisconnect = function( callback ){
//	window.onbeforeunload = callback;
/*
	window.addEventListener("beforeunload", function (e) {
		callback();
		return null;
	});
*/
	var myEvent = window.attachEvent || window.addEventListener;
	var chkevent = window.attachEvent ? 'onbeforeunload' : 'beforeunload'; /// make IE7, IE8 compitable
	myEvent(chkevent, function(e) { // For >=IE7, Chrome, Firefox
		callback();
	});
}

DataMcFly.prototype.on = function( key, callback ){
	if( key == 'value' ){
		this.documents(this.query,callback);
		//	once the initial query finishes, listen for any new records...
//		this.on('added', callback);
	}else{
		var self = this;

		this.socket.on( key, function(res){
			if( key == 'added' || key == 'changed' || key == 'removed' || key == 'online' ){
				var data = self.processData( res );
				self.currentItem = data;
			}else{
				var data = res;
			}
			callback( data );
		});
	}
};

DataMcFly.prototype.once = function( key, callback ){
	if( key == 'value' ){
		//	check query based on other functions.. 
		this.listDocuments(callback,this.query);
	}else{
		var self = this;
		
		this.socket.on( key, function(res){
			if( key == 'added' || key == 'changed' || key == 'removed' || key == 'online' ){
				var data = self.processData( res );
				self.currentItem = data;
			}else{
				var data = res;
			}
			callback( data );
			return true;
		});
	}
};

//	Send message to notification server...
DataMcFly.prototype.trigger = function(event, message){
	http.get(this.push_uri+'/emit/' + this.room + '/' + event + '/' + message, function(res) {
//		console.log("Got response: " + res.statusCode);
	}).on('error', function(e) {
//		console.log("Got error: " + e.message);
	});
};
DataMcFly.prototype.emit  = function(event, message) {
	http.get(this.push_uri+'/emit/' + this.room + '/' + event + '/' + message, function(res) {
//		console.log("Got response: " + res.statusCode);
	}).on('error', function(e) {
//		console.log("Got error: " + e.message);
	});
};


DataMcFly.prototype.connect = function(path, method, data, params, callback) {

	var self = this;

	if (path[0] === '/')
		path = path.substring(1);

	var uri = self.uri;
	var type = typeof(data);
	var isObject = type === 'object' || type === 'array';

	var headers = {};

	headers['Content-Type'] = isObject ? 'application/json' : 'text/plain';
	headers['X-DataMcFly-API-Key'] = self.apikey;
	var location = '';

	if (path[0] === '#')
		location = path.substring(1);
	else
		location = uri.pathname + path;

	var options = { 
			protocol: uri.protocol, 
			auth: uri.auth, 
			method: method || 'GET', 
			hostname: uri.hostname, 
			port: uri.port, 
			path: location + toParams(params, self.apikey), 
			agent:false, 
			headers: headers 
	};

//	console.log( location + toParams(params, self.apikey));

	var response = function (res) {
		var buffer = '';

		res.on('data', function(chunk) {
			buffer += chunk.toString('utf8');
		})

		req.setTimeout(exports.timeout, function() {
			callback(null,new Error('timeout'));
		});

		res.on('end', function() {
			var data = parseJSON(buffer.trim());
			var error = null;

			if (res.statusCode > 200) {				
				error = new Error(res.statusCode + ' (' + (data.message || '') + ') ');
				data = null;
			}

			var data = self.processData( data );

			self.currentItem = data;
			callback(data,error);
		});
	};

	var con = options.protocol === 'http:' ? http : http;
	var req = callback ? con.request(options, response) : con.request(options);

	req.on('error', function(err) {
		callback(null,err);
	});

	if (isObject)
		req.end(JSON.stringify(data));
	else
		req.end();

	return self;
};

