DataMcFly.prototype.transaction = function( updateFunction, cb){
	callback = cb || function(){};
	var c = {
		update: updateFunction,
		status: null
	};
	var e = this.currentItem.value();
	var e = e[0];
	var d = c.update( e );
	if ( is_void(d) ) {
		console.log("transaction failed: Data returned " + d);
		c.status = 1;
	}else{
		if( is_object( d ) ){
//	merge the arrays together...
			var k = merge( e, d );
			this.push( k, cb );
		}else{
//	returned null.. so delete...
			var id = e._id;
//			delete the record since it was NULL..
			this.deleteId( e._id, cb );
		}
		console.log( d );
	}
};

//	format return values into a object with helper functions...
DataMcFly.prototype.processData = function ( data ){
	var self = this;
	var retVal = [];
	var raw = data;

	var IS_JSON = true;
	try{
		var json = JSON.parse( data );
	}catch(err){
		IS_JSON = false;
	}

	if( IS_JSON ){
		var toProcess = json;
	}else{
		var toProcess = data;
	}

	if( !toProcess.length ){
		//	single variable...
		var obj = {
			'data': toProcess,
			value : function() {
				return this.data;
			},
			key : function(){
				return this.data._id;
			},
			ref : function(){
				return self;
			}
		};
		var Processed = obj;
	}else{
		for (var i in toProcess) {
			var value = toProcess[i];
			var obj = {
				'data': value,
				value : function() {
					return this.data;
				},
				key : function(){
					return this.data._id;
				},
				ref : function(){
					return self;
				}
			};
			retVal[i] = obj;
		}
		var Processed = retVal;
	}				
	
//	single variable...
	var data = {
		'data': Processed,
		'raw': raw,
		value : function() {
			if( this.count() > 1 ){
				return this.data;
			}else{
				return this.data.value();
			}
		},
		key : function(){
			if( this.count() > 1 ){
				return null;
			}else{
				return this.data.key();
			}
		},
		first : function(){
			if( this.count() > 0 ){
				var d = this.data[ 0 ];
				return d;
			}else{
				return this.data;				
			}
		},
		count : function(){
			if (typeof this.data.length == 'undefined' ){
				return null;
			}else{
				return this.data.length;	
			}
		},
		forEach : function( cb ){
			// iterate through each record returned...
			for (var i in this.data ) {
				var d = this.data[i];

				cb( d );
			}
		},
		ref : function(){
			return self;
		}
	};
	
	return data;
};

//	Utility Functions ---------------------------------------------------------------

var hb = function() {
	var a = 1;
	return function() {
		return a++
	}
}();

function is_object(a) {
	var b = typeof a;
	return "object" == b && null != a || "function" == b
}

function is_void(a) {
//	return (typeof a === 'undefined');
	return void 0 === a;
}

function merge() {
	var obj = {}, i = 0, il = arguments.length, key;
	for (; i < il; i++) {
		for (key in arguments[i]) {
			if (arguments[i].hasOwnProperty(key)) {
				obj[key] = arguments[i][key];
			}
		}
	}
	return obj;
};



